configfiles.configfile:
################################# load libraries and read file #################
library(Cardinal) library(gridExtra)
@READING_MSIDATA_FULLY_COMPATIBLE@
#if str($reference_type.reference) == "roi_ref": ## read and extract x,y,annotation information input_tabular <- read.delim("$reference_type.annotation_file", header = $reference_type.tabular_header, stringsAsFactors = FALSE) annotation_input <- input_tabular[,c($reference_type.column_x, $reference_type.column_y, $reference_type.column_names)] annotation_name <- colnames(annotation_input)[3] ##extract header for annotations to later export tabular with same name colnames(annotation_input) <- c("x", "y", "annotation") ## rename annotations header to default name "annotation"
## merge with coordinate information of msidata msidata_coordinates <- data.frame(coord(msidata)\$x, coord(msidata)\$y, c(1:ncol(msidata))) colnames(msidata_coordinates) <- c("x", "y", "pixel_index") merged_annotation <- merge(msidata_coordinates, annotation_input, by=c("x", "y"), all.x=TRUE) merged_annotation[is.na(merged_annotation)] <- "NA" merged_annotation <- merged_annotation[order(merged_annotation\$pixel_index),] msidata\$annotation <- as.character(merged_annotation[,4]) reference_group <- msidata\$annotation == "$reference_type.ref_name" #end if
## remove duplicated coordinates msidata <- msidata[,!duplicated(coord(msidata))]
@DATA_PROPERTIES_INRAM@
######################################## PDF ################################### ################################################################################ ################################################################################
pdf("colocalization.pdf", fonts = "Times", pointsize = 12) plot(0,type='n',axes=FALSE,ann=FALSE)
title(main=paste0("Colocalization for file: \n\n", "$infile.display_name"))
grid.table(property_df, rows= NULL)
if (npeaks > 0 && sum(is.na(spectra(msidata)))==0) {
## colocalization analysis col_results <- colocalized(msidata, #if str($reference_type.reference) == "mz_ref": mz = $reference_type.mz_value, #else ref = reference_group, #end if n = $n_tophits, sort.by = "$sort_by", threshold = median)
## Summary results table col_results_df <- as.data.frame(col_results)
colnames(col_results_df) <- c("i", "mz","Pearson", "Manders' overlap", "M1", "M2", "Dice similarity")
write.table(col_results_df, file="$coloc_results", quote = FALSE, row.names = FALSE, col.names=TRUE, sep = "\t")
## visualize top 10 m/z features
if (nrow(col_results_df) <= 10) { for (mass in col_results_df\$mz){ par(oma=c(0,0,0,1))## margin for image legend print(image(msidata, mz=mass, plusminus=0.25, main= paste0(round(mass, digits = 2)), contrast.enhance = "histogram", strip=FALSE, ylim= c(maximumy,minimumy))) } }else{ for (mass in col_results_df\$mz[1:10]){ par(oma=c(0,0,0,1))## margin for image legend print(image(msidata, mz=mass, plusminus=0.25, main= paste0(round(mass, digits = 2)), contrast.enhance = "histogram", strip=FALSE, ylim= c(maximumy,minimumy))) } }
dev.off()
}else{ print("Inputfile has no intensities > 0") }
