tool.command:
@CMD_IMPORTS@
@LINK_SCAN_INPUTS@
@LINK_FASTA_INPUT@
@LINK_TARGET_FASTA@
@LINK_LIB_INPUT@
for SCAN_FILE in `ls -1 inputs/*`; do 
echo "\$SCAN_FILE" &&
EncyclopeDIA -Djava.awt.headless=true -Xmx\$[ \${GALAXY_MEMORY_MB:-20480} / 1024 ]g 
-i \$SCAN_FILE
@FASTA_INPUT@
@TARGET_FASTA@
@LIB_INPUT@
@COMMON_OPTIONS@
@MASS_LIBRARY_TOLERANCE@
@PERCOLATOR_OPTIONS@
@PEAK_OPTIONS@
@WINDOW_OPTIONS@
@MODIFICATION_OPTIONS@
@SEARCH_OPTIONS@ | tee -a search2lib.log
; done && 
for TXT in `find inputs/*.mzML.[efw]*[ast].txt`; do TRGT=`echo \$TXT | sed 's/mzML/dia/'`; ln -s \$TXT \$TRGT; done &&
EncyclopeDIA -Djava.awt.headless=true -Xmx\$[ \${GALAXY_MEMORY_MB:-20480} / 1024 ]g -libexport
@SCAN_INPUTS@
@FASTA_INPUT@
@TARGET_FASTA@
@LIB_INPUT@
-a $a
-o chromatogram_library.elib
&& ls -l ./*.* inputs/*
&& cat chromatogram_library.elib.peptides.txt
&& cat chromatogram_library.elib.proteins.txt
| tee -a search2lib.log
