tool.command:
@CMD_IMPORTS@
@LINK_SCAN_INPUTS@
@LINK_FASTA_INPUT@
@LINK_TARGET_FASTA@
for SCAN_FILE in `ls -1 inputs/*`; do 
echo "\$SCAN_FILE" &&
EncyclopeDIA -Djava.awt.headless=true -Xmx12g $search_app
-i \$SCAN_FILE
@FASTA_INPUT@
@TARGET_FASTA@
@COMMON_OPTIONS@
@MASS_LIBRARY_TOLERANCE@
@PERCOLATOR_OPTIONS@
@PEAK_OPTIONS@
@WINDOW_OPTIONS@
@MODIFICATION_OPTIONS@
@SEARCH_OPTIONS@ | tee -a search2lib.log
; done && 
EncyclopeDIA -Djava.awt.headless=true -Xmx12g -libexport
$search_app
@SCAN_INPUTS@
@FASTA_INPUT@
@TARGET_FASTA@
@COMMON_OPTIONS@
@MASS_LIBRARY_TOLERANCE@
@PERCOLATOR_OPTIONS@
@PEAK_OPTIONS@
@WINDOW_OPTIONS@
@MODIFICATION_OPTIONS@
@SEARCH_OPTIONS@
-o gxpedia
| tee -a search2lib.log
