tool.command:
@QUOTE_FOO@ @EXT_FOO@ #import re
## Preprocessing mkdir tr && ln -s '$tr' 'tr/${re.sub("[^\w\-_]", "_", $tr.element_identifier)}.$gxy2omsext($tr.ext)' && mkdir swath_files && mkdir ${' '.join(["'swath_files/%s'" % (i) for i, f in enumerate($swath_files) if f])} && ${' '.join(["ln -s '%s' 'swath_files/%s/%s.%s' && " % (f, i, re.sub('[^\w\-_]', '_', f.element_identifier), $gxy2omsext(f.ext)) for i, f in enumerate($swath_files) if f])} mkdir output_files && mkdir ${' '.join(["'output_files/%s'" % (i) for i, f in enumerate($swath_files) if f])} &&
## Main program call
set -o pipefail && @EXECUTABLE@ -write_ctd ./ && python3 '$__tool_directory__/fill_ctd.py' '@EXECUTABLE@.ctd' '$args_json' '$hardcoded_json' && @EXECUTABLE@ -ini @EXECUTABLE@.ctd -tr 'tr/${re.sub("[^\w\-_]", "_", $tr.element_identifier)}.$gxy2omsext($tr.ext)' -swath_files ${' '.join(["'swath_files/%s/%s.%s'"%(i, re.sub('[^\w\-_]', '_', f.element_identifier), $gxy2omsext(f.ext)) for i, f in enumerate($swath_files) if f])} -output_files ${' '.join(["'output_files/%s/%s.%s'"%(i, re.sub('[^\w\-_]', '_', f.element_identifier), $gxy2omsext("tabular")) for i, f in enumerate($swath_files) if f])}
## Postprocessing ${' '.join(["&& mv -n 'output_files/%(bn)s/%(id)s.%(gext)s' 'output_files/%(bn)s/%(id)s'"%{"bn": i, "id": re.sub('[^\w\-_]', '_', f.element_identifier), "gext": $gxy2omsext("tabular")} for i, f in enumerate($swath_files) if f])} #if "ctd_out_FLAG" in $OPTIONAL_OUTPUTS && mv '@EXECUTABLE@.ctd' '$ctd_out' #end if
